mapper.update:
update book set title=#{title }, subTitle=#{subTitle}, cate1=#{cate1 }, cate2=#{cate2 }, author=#{author }	
, pub=#{pub }
, price=#{price }
, d_price=#{d_price }
, p_date=#{p_date }
, book_index=#{book_index }
, page=#{page }
, weight=#{weight }
, book_size=#{book_size }
, isbn13=#{isbn13 }
, isbn10=#{isbn10 }
, info=#{info }
, img1=#{img1 }
, img2=#{img2 }
, img3=#{img3 }
, img4=#{img4 }
, img5=#{img5 }
, img6=#{img6 }
, img7=#{img7 }
, stock=#{stock }
, point=#{point }
, logtime=#{sysdate }			
WHERE seq=#{book_id}
