mapper.select:
SELECT * FROM (SELECT rownum rn, tt.* from
(SELECT book.*, score FROM book LEFT JOIN view_score ON book.seq = view_score.book_id WHERE 1=1
AND (title LIKE '%' || #{titleOrAuthor} || '%' OR author LIKE '%' || #{titleOrAuthor} || '%')
AND title LIKE '%' || #{title} || '%'
AND author LIKE '%' || #{author} || '%'
AND pub LIKE '%' || #{pub} || '%'
AND to_char(to_date(p_date, 'YYYY"년" MM"월" DD"일"'), 'YYYYMM') = #{p_date}
AND cate1 = #{cate1}
AND (d_price <= #{min} AND d_price <= #{max})
ORDER BY logtime DESC
ORDER BY logtime DESC ORDER BY d_price ORDER BY d_price DESC ORDER BY nvl(score, 0) DESC ORDER BY to_date(p_date, 'YYYY"년" MM"월" DD"일"') DESC
)tt)
WHERE rn >= #{startNum} AND rn <= #{endNum}
